{
  "name": "Document Approval",
  "type": "approval",
  "description": "Multi-level document approval process",
  "steps": [
    {
      "id": 1,
      "name": "Manager Review",
      "type": "approval",
      "config": {
        "approver": "manager",
        "timeout": "48h",
        "escalateTo": "senior_manager"
      }
    },
    {
      "id": 2,
      "name": "Finance Approval",
      "type": "approval",
      "config": {
        "approver": "finance_team",
        "requiredApprovals": 2,
        "timeout": "72h"
      }
    },
    {
      "id": 3,
      "name": "Final Sign-off",
      "type": "approval",
      "config": {
        "approver": "director",
        "timeout": "24h"
      }
    }
  ],
  "triggers": [
    {
      "type": "manual",
      "action": "submit_document"
    }
  ],
  "status": "active",
  "priority": "medium",
  "createdBy": "postman-user"
}

{
  "name": "Daily Data Sync",
  "type": "automation",
  "description": "Synchronize data between systems daily",
  "steps": [
    {
      "id": 1,
      "name": "Fetch Source Data",
      "type": "api_call",
      "config": {
        "endpoint": "https://api.source.com/data",
        "method": "GET",
        "auth": "bearer_token"
      }
    },
    {
      "id": 2,
      "name": "Transform Data",
      "type": "script",
      "config": {
        "function": "transformToDestinationFormat",
        "validations": ["required_fields", "data_types"]
      }
    },
    {
      "id": 3,
      "name": "Update Destination",
      "type": "api_call",
      "config": {
        "endpoint": "https://api.destination.com/update",
        "method": "POST",
        "retries": 3
      }
    },
    {
      "id": 4,
      "name": "Send Report",
      "type": "notification",
      "config": {
        "type": "email",
        "to": "admin@example.com",
        "template": "sync_report"
      }
    }
  ],
  "triggers": [
    {
      "type": "schedule",
      "cron": "0 2 * * *",
      "timezone": "UTC"
    }
  ],
  "status": "active",
  "priority": "high",
  "createdBy": "postman-user"
}
